  `pd_id` varchar(60) DEFAULT NULL COMMENT '流程ID',
  `module_code` int(11) DEFAULT NULL,
  `process_users` varchar(255) DEFAULT '',
  `is_D02` smallint(1) DEFAULT '0' COMMENT '是否d02物料 0 不是 1 是，影响取合同逻辑',
  PRIMARY KEY (`id`),
  UNIQUE KEY `index_plan_code` (`code`) USING BTREE,
  KEY `index_plan_request_userid` (`req_user_id`),
  KEY `index_plan_status` (`status`)
) ENGINE=InnoDB AUTO_INCREMENT=6975 DEFAULT CHARSET=utf8;

