DROP PROCEDURE IF EXISTS `bid_count_rank_61`;
DELIMITER ;;
CREATE DEFINER=`gsp`@`localhost` PROCEDURE `bid_count_rank_61`(v_v int)
BEGIN
declare d_rfx_head_id               int; 
declare d_rfx_head_name             varchar(128);
declare d_bid_currency_id           int;
declare d_bid_currency_name         varchar(32);
declare d_fc_history_cost_amount    double; 
declare d_fc_init_amount            double; 
declare d_bid_count_num             int default 0;
declare d_operate_time              varchar(32);
declare d_is_have_enabled_bid       int;
declare d_bid_type_id               int;
declare d_superid                   int;
declare flag int;

declare count_rfx_amount cursor for 
        select rfx_head_id,rfx_head_name,bid_currency_id,bid_currency_name,fc_history_cost_amount,fc_init_amount
        from (select rfx_head_id,rfx_head_name,bid_currency_id,bid_currency_name,sum(fc_history_cost_amount) 
        as fc_history_cost_amount,sum(fc_init_amount) as fc_init_amount
        from (select distinct rfx_head_id,rfx_head_name,bid_currency_id,bid_currency_name,passel_id,passel_name,
        item_id,item_name,fc_history_cost_amount,fc_init_amount from tmp_price_record
        where is_main=2  and rfx_head_id=v_v) as a group by rfx_head_id,rfx_head_name,bid_currency_id,
        bid_currency_name) as a where rfx_head_id=v_v order by rfx_head_id,rfx_head_name,bid_currency_id,
        bid_currency_name;
declare exit handler for 1329 set flag=3;
 
set d_bid_type_id:=1;
set d_superid:=-1;
update bid_rfx_price_note set currency_id=1 where currency_id=0 and rfx_head_id=v_v;
commit;

select sum(bid_count_num) into d_bid_count_num from tmp_price_record where is_main=2 and rfx_head_id=v_v;

select max(operate_time) into d_operate_time from tmp_price_record where rfx_head_id=v_v;

select case when count(1)>0 then 2 else 1 end into d_is_have_enabled_bid from tmp_price_record where rfx_head_id=v_v and is_have_enabled_bid=2;
open count_rfx_amount;
loop 
fetch count_rfx_amount into d_rfx_head_id,d_rfx_head_name,d_bid_currency_id,d_bid_currency_name,
d_fc_history_cost_amount,d_fc_init_amount;

insert into tmp_price_rank values (0,'',d_rfx_head_id,0,0,0,0,d_fc_history_cost_amount,0,0,0,d_rfx_head_name,'','',0,
d_superid,0,'',0,d_bid_currency_id,d_bid_currency_name,0,0,0,1,d_bid_count_num,0,d_rfx_head_name,
d_bid_type_id,d_is_have_enabled_bid,d_operate_time,now(),0,d_fc_init_amount);
end loop;
close count_rfx_amount;
END
;;
DELIMITER ;

