DELIMITER ;;
CREATE DEFINER=`gsp`@`localhost` PROCEDURE `bid_count_rank_611`(v_rfx_head_id int,v_passel_id int,v_item_id int)
begin
declare d_v_vendor_id           int;
declare d_vendor_name           varchar(256); 
declare d_rfx_head_id           int; 
declare d_fc_min_require_num    int;
declare d_fc_history_cost        double; 
declare d_fc_history_cost_amount double; 
declare d_fc_init_price             double; 
declare d_fc_init_amount            double; 
declare d_is_supply_bid         int; 
declare d_rfx_item_price        double; 
declare d_fc_min_require_amount double;
declare d_rfx_head_name         varchar(128);
declare d_passel_id             int;  
declare d_item_id               int; 
declare d_passel_name           varchar(128); 
declare d_item_name             varchar(256);
declare d_is_main               int; 
declare d_superid               int;
declare d_vendor_currency_id    int;
declare d_vendor_currency_name  varchar(32); 
declare d_rate                  double;
