                                 bid_rfx_rate.rfx_head_id
                             and bid_base_company.currency_id =
                                 bid_rfx_rate.currency_id
                             and r_state = 2) d,
                         (select rfx_head_id,
                                 currency_id as bid_currency_id,
                                 currency_name as bid_currency_name
                            from bid_rfx_rate
                           where rfx_head_id = rfx_head_id
                             and is_bid_currency = 2) e
                   where a.rfx_head_id = b.rfx_head_id
                     and a.item_id = b.item_id
                     and a.rfx_head_id = c.rfx_head_id
                     and a.item_id = c.item_id
                     
                     and a.rfx_head_id = d.rfx_head_id
                     and a.rfx_head_id = e.rfx_head_id) aa
            left join((select t.*,
                             bid_rfx_price_note.rfx_item_price,
                             bid_rfx_price_note.is_supply_bid,
                             bid_rfx_price_note.currency_id,
                             bid_rfx_price_note.operate_time,
                             ifnull(rfx_born_price, 0) as rfx_born_price,
                             ifnull(rfx_base_price, 0) as rfx_base_price,
                             ifnull(discount_rate, 0) as discount_rate
                        from bid_rfx_price_note,
                             (select ifnull(max(note_id), 0) as note_id,
                                     vendor_id,
                                     rfx_head_id,
                                     passel_id,
                                     item_id
                                from bid_rfx_price_note
                               where iscan = 2
                                 and rfx_head_id = v_rfx_head_id
                               group by vendor_id,
                                        rfx_head_id,
                                        passel_id,
                                        item_id) t
                       where bid_rfx_price_note.vendor_id = t.vendor_id
                         and bid_rfx_price_note.rfx_head_id = t.rfx_head_id
                         and bid_rfx_price_note.passel_id = t.passel_id
                         and bid_rfx_price_note.item_id = t.item_id
                         and bid_rfx_price_note.note_id = t.note_id) bb, (select bid_rfx_price_note.rfx_head_id,
                                                                                bid_rfx_price_note.vendor_id,
                                                                                bid_rfx_price_note.passel_id,
                                                                                bid_rfx_price_note.item_id,
                                                                                count(*) as bid_count_num
                                                                           from bid_rfx_price_note,
                                                                                bid_rfx_item
                                                                          where bid_rfx_price_note.rfx_head_id =
                                                                                bid_rfx_item.rfx_head_id
                                                                            and bid_rfx_price_note.passel_id =
                                                                                bid_rfx_item.passel_id
                                                                            and bid_rfx_price_note.item_id =
                                                                                bid_rfx_item.item_id
                                                                            and bid_rfx_price_note.rfx_head_id =
                                                                                v_rfx_head_id
                                                                            and bid_rfx_item.is_main = 2
                                                                          group by bid_rfx_price_note.rfx_head_id,
                                                                                   bid_rfx_price_note.vendor_id,
                                                                                   bid_rfx_price_note.passel_id,
                                                                                   bid_rfx_price_note.item_id) cc) on (aa.rfx_head_id =
                                                                                                                      bb.rfx_head_id and
                                                                                                                      aa.passel_id =
                                                                                                                      bb.passel_id and
                                                                                                                      aa.item_id =
                                                                                                                      bb.item_id and
                                                                                                                      aa.vendor_id =
                                                                                                                      bb.vendor_id and
                                                                                                                      aa.vendor_currency_id =
                                                                                                                      bb.currency_id and
                                                                                                                      aa.rfx_head_id =
                                                                                                                      cc.rfx_head_id and
                                                                                                                      aa.passel_id =
                                                                                                                      cc.passel_id and
                                                                                                                      aa.item_id =
                                                                                                                      cc.item_id and
                                                                                                                      aa.vendor_id =
                                                                                                                      cc.vendor_id)) aaa
    left join((select vendor_id,
                      rfx_head_id,
                      passel_id,
                      item_id,
                      case
                        when enabled_bid > 0 then
                         2
                        else
                         1
                      end is_have_enabled_bid
                 from (select vendor_id,
                              rfx_head_id,
                              passel_id,
                              item_id,
                              count(1) as enabled_bid
                         from bid_rfx_price_note
                        where rfx_head_id = v_rfx_head_id
                          and iscan = 2
                        group by vendor_id, rfx_head_id, passel_id, item_id) as cn) ccc) on (aaa.rfx_head_id =
                                                                                            ccc.rfx_head_id and
                                                                                            aaa.passel_id =
                                                                                            ccc.passel_id and
                                                                                            aaa.item_id =
                                                                                            ccc.item_id and
                                                                                            aaa.vendor_id =
                                                                                            ccc.vendor_id),
   bid_rfx_head brh,
   bid_rfx_passel brp,
   bid_rfx_item bri
   where aaa.rfx_head_id = brh.rfx_head_id
     and aaa.passel_id = brp.passel_id
     and aaa.rfx_head_id = brp.rfx_head_id
     and aaa.rfx_head_id = bri.rfx_head_id
     and aaa.passel_id = bri.passel_id
     and aaa.item_id = bri.item_id;
 
create  temporary table if not exists temp_price_record as  select  * from tmp_price_record where 1<>1 ;
truncate table  temp_price_record ;
insert into temp_price_record
select * from  tmp_price_record ;
    
 call bid_count_rank_proc_55_1(v_rfx_head_id);
 call bid_count_rank_proc_55_2(v_rfx_head_id);
 call bid_count_rank_proc_55_3(v_rfx_head_id);
 call bid_count_rank_proc_55_4(v_rfx_head_id);
 call bid_count_rank_proc_55_6(v_rfx_head_id);
 call bid_count_rank_proc_55_7(v_rfx_head_id);
 call bid_count_rank_proc_55_9(v_rfx_head_id);
 call bid_count_rank_proc_55_10(v_rfx_head_id);
 call bid_count_rank_proc_55_12(v_rfx_head_id);
end
;;
DELIMITER ;

