DROP PROCEDURE IF EXISTS `bid_count_rank_proc_55_1`;
DELIMITER ;;
CREATE DEFINER=`gsp`@`localhost` PROCEDURE `bid_count_rank_proc_55_1`(v_v int)
BEGIN
