DELIMITER ;;
CREATE DEFINER=`gsp`@`localhost` PROCEDURE `bid_count_rank_proc_55_11`(v_rfx_head_id int,v_passel_id int,v_item_id int)
begin
declare d_v_vendor_id           int;
declare d_vendor_name           varchar(256); 
declare d_rfx_head_id           int; 
