declare d_last_rank             int;
declare d_rank_seq              int; 
declare d_last_fc_min_require_amount double;
declare d_bid_type_id             int;
declare v_setting_bid_coefficient  int;  
declare v_is_allow_zero           int; 
declare flag           int; 
declare count_low_item_amount_rank cursor for
select  * from (select vendor_id,vendor_name,tmp.rfx_head_id,fc_min_require_num,fc_history_cost,
        fc_history_cost_amount,fc_init_price,fc_init_amount,is_supply_bid,rfx_item_price,fc_min_require_amount,
        tmp.rfx_head_name,tmp.item_id,tmp.passel_id,tmp.passel_name,tmp.item_name,bri.is_main,vendor_currency_id,
        vendor_currency_name,rate,bid_currency_id,bid_currency_name,bid_count_num,rfx_born_price,rfx_base_price,
        discount_rate,case when ifnull(bbc.bid_coefficient_value,1)<=0 then 1 else ifnull(bbc.bid_coefficient_value,1) end  bid_coefficient_value
        from tmp_price_record tmp, bid_rfx_item bri,bid_base_company bbc
        where bri.is_main<>2 and bri.passel_id=v_passel_id and bri.item_id=v_item_id and bri.rfx_head_id=v_rfx_head_id
        and tmp.rfx_head_id=bri.rfx_head_id and tmp.passel_id=bri.passel_id and tmp.item_id=bri.item_id
        and tmp.rfx_head_id=bbc.rfx_head_id and tmp.vendor_id=bbc.company_id and bbc.r_state=2  

        and tmp.vendor_id not in (select concat(passel_id,'_',item_id,'_') vendor_id from temp_price_record
        where is_main = 2 and rfx_head_id=v_rfx_head_id and passel_id=v_passel_id and item_id =v_item_id  
        and is_have_enabled_bid = 1) 

) as a
        order by rfx_head_id,passel_id,item_id,fc_min_require_amount*bid_coefficient_value,vendor_id;
declare exit handler for 1329 set flag=3;

select max(bid_type_id) into d_bid_type_id from tmp_price_rank where rfx_head_id=v_rfx_head_id;

select ifnull(is_setting_bid_coefficient,1),ifnull(is_allow_zero,1) into v_setting_bid_coefficient,v_is_allow_zero 
from bid_rfx_line where rfx_head_id=v_rfx_head_id;
set d_last_rank:=0;
set d_rank_seq:=1;
