     d_fc_history_cost,d_fc_history_cost_amount,d_fc_init_price,d_fc_init_amount,d_is_supply_bid,d_rfx_item_price,
     d_fc_min_require_amount,d_rfx_head_name,d_item_id,d_passel_id,d_passel_name,d_item_name,d_is_main,
     d_vendor_currency_id,d_vendor_currency_name,d_rate,d_bid_currency_id,d_bid_currency_name,d_bid_count_num,
