           if(d_fc_min_require_amount=d_last_fc_min_require_amount and v_is_allow_zero<>2) then
              set d_rank:=d_last_rank;
           else
              set d_rank:=d_rank_seq;
           end if;
        set d_last_fc_min_require_amount:=d_fc_min_require_amount;
        else 
