     set d_last_rank:=d_rank;
     set d_bid_type_id:=d_bid_type_id+1;
     select max(operate_time) into d_operate_time from tmp_price_record where vendor_id=d_v_vendor_id
     and item_id=d_item_id and passel_id=d_passel_id and rfx_head_id=v_rfx_head_id;
     select case when count(1)>0 then 2 else 1 end is_have_enabled_bid into d_is_have_enabled_bid from
     tmp_price_record where ifnull(is_have_enabled_bid,0)=2 and passel_id=d_passel_id
     and item_id=d_item_id and vendor_id=d_v_vendor_id and rfx_head_id=v_rfx_head_id;
     
