     d_fc_min_require_amount,d_rfx_head_name,d_passel_name,d_item_name,d_is_main,d_superid,d_vendor_currency_id,
     d_vendor_currency_name,d_rate,d_bid_currency_id,d_bid_currency_name,d_rfx_born_price,d_discount_rate,
     d_discount_rate,2,d_bid_count_num,d_rank,'',d_bid_type_id,d_is_have_enabled_bid,d_operate_time,
     sysdate(),d_fc_init_price,d_fc_init_amount);
     set d_rank:=d_rank+1;
     set d_rank_seq:=d_rank_seq+1;
   end loop;
close count_low_item_amount_rank;
    end
;;
DELIMITER ;

