 FC_HISTORY_COST,FC_HISTORY_COST_AMOUNT,IS_SUPPLY_BID,RFX_ITEM_PRICE,FC_MIN_REQUIRE_AMOUNT,RFX_HEAD_NAME,
 PASSEL_NAME,ITEM_NAME,IS_MAIN,SUPERID,VENDOR_CURRENCY_ID,VENDOR_CURRENCY_NAME,RATE,BID_CURRENCY_ID,
 BID_CURRENCY_NAME,RFX_BORN_PRICE,RFX_BASE_PRICE,DISCOUNT_RATE,IS_LEAF,BID_COUNT_NUM,RANK,BID_TYPE_NAME,
 BID_TYPE_ID,IS_HAVE_ENABLED_BID,OPERATE_TIME,COUNTRANK_TIME,FC_INIT_PRICE,FC_INIT_AMOUNT) select * from tmp_price_rank ;
