declare d_bid_coefficient_value      int ; 
declare d_fc_min_require_amount        double;
declare v_setting_bid_coefficient     int;  
declare v_is_allow_zero               int; 
declare d_v_passel_id           int;
declare d_passel_name           varchar(128); 
declare flag int;

declare count_rfx_amount_rank cursor for
select * from (select t.rfx_head_id,t.rfx_head_name,vendor_id,vendor_name,bid_currency_id,bid_currency_name,
        vendor_currency_id,vendor_currency_name,rate,
        case when ifnull(bbc.bid_coefficient_value,1)<=0 then 1 else ifnull(bbc.bid_coefficient_value,1) end bid_coefficient_value,
        sum(bid_count_num) as bid_count_num,
        sum(fc_history_cost_amount) as fc_history_cost_amount,
        sum(fc_init_amount) as fc_init_amount,
        sum(fc_min_require_amount) as fc_min_require_amount
        from tmp_price_record t, bid_base_company bbc
