        and t.vendor_id not in (select vendor_id from temp_price_record where is_main=2 and rfx_head_id=v_v
        and is_have_enabled_bid=1)

        group by t.rfx_head_id,t.rfx_head_name,vendor_id,
        vendor_name,bid_currency_id,bid_currency_name,vendor_currency_id,vendor_currency_name,rate,
        bid_coefficient_value) as a order by rfx_head_id,fc_min_require_amount*bid_coefficient_value,vendor_id;

