set d_superid:=d_bid_type_id;
set d_last_rank:=0;
set d_last_fc_min_require_amount:=0;
set d_rank:=1;
set d_rank_seq:=1;
open  count_rfx_amount_rank;
loop
fetch count_rfx_amount_rank into d_rfx_head_id,d_rfx_head_name,d_vendor_id,d_vendor_name,d_bid_currency_id,d_bid_currency_name,
d_vendor_currency_id,d_vendor_currency_name,d_rate,d_bid_coefficient_value,d_bid_count_num,d_fc_history_cost_amount
,d_fc_init_amount,d_fc_min_require_amount;

if  v_setting_bid_coefficient<>2 then 
    if(d_fc_min_require_amount=d_last_fc_min_require_amount and v_is_allow_zero<>2) then
           set d_rank:=d_last_rank;
    else
           set d_rank:=d_rank_seq;
    end if;
           set d_last_fc_min_require_amount:=d_fc_min_require_amount;
