           best_saving_percent,fc_min_require_amount,cost_saving_amount,cost_saving_percent,vendor_number,
           rfx_begin_time,rfx_end_time,create_by,create_time,istest)   
           select rh.rfx_head_id,rh.rfx_head_name,rh.rfx_type,rh.company_id,ci.company_name,rh.rfx_oper_unit,
           co.company_org_name,br.fc_history_cost_amount,zj.best_bid_amount,
           (br.fc_history_cost_amount - zj.best_bid_amount) * 1 best_saving_amount,
           100 * round((br.fc_history_cost_amount - zj.best_bid_amount)*if (rh.rfx_type=6,-1,if(rh.rfx_type=5,1,0)) /
           br.fc_history_cost_amount,4) best_saving_percent,br.fc_min_require_amount,
           (br.fc_history_cost_amount - br.fc_min_require_amount) *if (rh.rfx_type=6,-1,if(rh.rfx_type=5,1,0)) cost_saving_amount,
           100 * round((fc_history_cost_amount - fc_min_require_amount) *if (rh.rfx_type=6,-1,if(rh.rfx_type=5,1,0)) /
           fc_history_cost_amount,4) cost_saving_percent,bid_vendor_number(rh.rfx_head_id) vendor_number,
           rh.rfx_begin_time,rh.rfx_end_time,rh.create_by,rh.create_time,rh.istest
           from bid_rfx_head rh,company_info ci,bid_rfx_rank br, ( select group_id as company_org_id,group_name  as company_org_name
                from sys_group sg where  sg.group_code like 'EBS%' and group_status=1 ) co,
           (select rfx_head_id,sum(fc_min_require_amount) best_bid_amount from bid_rfx_rank where 1 = 1
           and item_id > 0 and rank = 1 and vendor_id > 0 and is_main = 2 group by rfx_head_id) zj
           where 1 = 1 and rh.company_id = ci.company_id and br.rfx_head_id = rh.rfx_head_id and br.passel_id = 0
           and rank = 1 and zj.rfx_head_id = rh.rfx_head_id and rh.rfx_state >= 1 and rh.rfx_type in (5, 6)
           and rh.rfx_oper_unit = co.company_org_id 
           and rh.rfx_head_id = b_rfx_head_id and rh.istest=1 order by rh.create_time desc;
        end if;
        fetch cur into b_rfx_head_id; 
        end while;
        close cur;
    end
;;
DELIMITER ;

