      declare cur cursor for select rh.rfx_head_id from bid_rfx_head rh where rh.rfx_head_id not in 
      (select vr.rfx_head_id from bid_rfx_vendor_report vr);
      declare continue handler for sqlstate '02000' set stop=1;  
      open cur;
      fetch cur into b_rfx_head_id; 
      while stop <> 1 do
        if b_rfx_head_id <> 0 then
           insert into bid_rfx_vendor_report (rfx_head_id,rfx_type,rfx_head_name,create_time,rfx_begin_time,
