           bbc.company_name vendor_name,cp.userid,cp.mobil,bbc.r_state,bbc.operate_time,
           bid_vendor_count(bbc.rfx_head_id, bbc.company_id) bid_rfx_seq,
           round(nvl(br.fc_min_require_amount, 0), 2) fc_min_require_amount,rh.company_id,ci.company_name,
           rh.rfx_oper_unit,co.company_org_name,rh.istest
           from bid_rfx_head rh,bid_base_company bbc,cpcuser cp,bid_rfx_rank br,company_info ci,company_org co,
           (select  rfx_head_id,vendor_id,max(bid_rfx_seq) bid_rfx_seq
