DELIMITER ;;
CREATE DEFINER=`gsp`@`%` PROCEDURE `MATERIEL_proc1`()
BEGIN  
    DECLARE MATERIEL_num VARCHAR(30);
		DECLARE BUYER VARCHAR(30);
DECLARE SALER VARCHAR(30);
		DECLARE done int(30);


    DECLARE MATERIEL_NOs CURSOR FOR SELECT
		l.MATERIEL_NO AS MATERIEL_num,
		r.BUYER,
		r.SALER
	FROM
		td_contract_public_line l
	LEFT JOIN td_contract_rise r ON r.ID = l.BASE_ID
	WHERE
		r.IS_FRAME = 'Y'
	AND r.CONTRACT_NUM IS NOT NULL
	AND l.MATERIEL_NO IS NOT NULL
	AND r.SAP_TYPE NOT IN ('UNSAP', 'GXWX', 'GXWXJJJ','WWWX','WTYE')
	AND r.PRICE_STATUS = 'PB00'
	GROUP BY
		l.MATERIEL_NO,
		r.buyer,
		r.saler;

    DECLARE CONTINUE HANDLER FOR NOT FOUND SET done = 1;
    OPEN  MATERIEL_NOs;
    
        read_loop: LOOP
            FETCH  NEXT from MATERIEL_NOs INTO MATERIEL_num,BUYER,SALER;
            IF done THEN
            LEAVE read_loop;
            END IF; 
					call MATERIEL_proc2(MATERIEL_num,buyer,saler);
			 
				SET done=0;
				END LOOP;
    
  CLOSE MATERIEL_NOs;
    
END
;;
DELIMITER ;

-- ----------------------------
-- Procedure structure for MATERIEL_proc2
-- ----------------------------
DROP PROCEDURE IF EXISTS `MATERIEL_proc2`;
DELIMITER ;;
CREATE DEFINER=`gsp`@`%` PROCEDURE `MATERIEL_proc2`(in MATERIEL_num VARCHAR(30), in buyer VARCHAR(30),in saler VARCHAR(30))
BEGIN  
    
		
		DECLARE done int(30);
		

		DECLARE MATERIEL_noone VARCHAR(30);
		DECLARE MATERIEL_notwo VARCHAR(30);
