DECLARE CREATE_TIMEtwo  date ;
DECLARE PRICE_STATUSone  VARCHAR(30) ;
DECLARE PRICE_STATUStwo  VARCHAR(30) ;
DECLARE price_one  VARCHAR(50) ;
DECLARE price_two  VARCHAR(50) ;

		DECLARE resultone CURSOR FOR sELECT l.MATERIEL_NO,l.MATERIEL_NAME,r.BUYER,r.SALER,
					r.CONTRACT_NUM,r.CONTRACT_STR,r.CONTRACT_END,r.CREATE_TIME,r.PRICE_STATUS,l.PPRICE_UNIT FROM td_contract_public_line l
				LEFT JOIN td_contract_rise r 
				ON r.ID = l.BASE_ID
				WHERE MATERIEL_NO = MATERIEL_num and  r.IS_FRAME = 'Y'
				AND r.CONTRACT_NUM IS NOT NULL
				AND r.buyer = buyer
				AND r.PRICE_STATUS = 'PB00'
				and r.saler =saler AND r.SAP_TYPE NOT IN ('UNSAP', 'GXWX', 'GXWXJJJ','WWWX','WTYE');
				

			DECLARE resulttwo CURSOR FOR sELECT l.MATERIEL_NO,l.MATERIEL_NAME,r.BUYER,r.SALER,
					r.CONTRACT_NUM,r.CONTRACT_STR,r.CONTRACT_END,r.CREATE_TIME,r.PRICE_STATUS,l.PPRICE_UNIT FROM td_contract_public_line l
				LEFT JOIN td_contract_rise r 
				ON r.ID = l.BASE_ID
				WHERE MATERIEL_NO = MATERIEL_num and  r.IS_FRAME = 'Y'
				AND r.CONTRACT_NUM IS NOT NULL
				AND r.buyer = buyer
				AND r.PRICE_STATUS = 'PB00'
				and r.saler =saler AND r.SAP_TYPE NOT IN ('UNSAP', 'GXWX', 'GXWXJJJ','WWWX','WTYE');
				

		OPEN  resultone;
			
					read_loopone: LOOP
							FETCH  NEXT from resultone INTO MATERIEL_NOone,MATERIEL_NAMEone,BUYERone,SALERone,
								CONTRACT_NUMone,CONTRACT_STRone,CONTRACT_ENDone,CREATE_TIMEone,PRICE_STATUSone,price_one;
							IF done THEN
							LEAVE read_loopone;
							END IF; 
				
					
										OPEN  resulttwo;
									
											read_looptwo: LOOP
													FETCH  NEXT from resulttwo INTO MATERIEL_NOtwo,MATERIEL_NAMEtwo,BUYERtwo,SALERtwo,
														CONTRACT_NUMtwo,CONTRACT_STRtwo,CONTRACT_ENDtwo,CREATE_TIMEtwo,PRICE_STATUStwo,price_two;
													IF done THEN
													LEAVE read_looptwo;
													END IF; 
										
											IF(((CONTRACT_STRtwo > CONTRACT_STRone && CONTRACT_STRtwo < CONTRACT_ENDone)||(CONTRACT_ENDtwo > CONTRACT_STRone && CONTRACT_ENDtwo < CONTRACT_ENDone ))
													&& price_one != price_two) THEN
													
													INSERT INTO td_contract_repeat_material (
														MATERIEL_NO,
