														CONTRACT_STRone,
														CONTRACT_ENDone,
														CREATE_TIMEone,
														PRICE_STATUSone);
													commit;
												
											END IF;

											SET done=0;
										END LOOP;
								

					SET done=0;
				END LOOP;

		CLOSE resulttwo;
    CLOSE resultone;
END
