DELIMITER ;;
CREATE DEFINER=`root`@`localhost` PROCEDURE `saveCk`(IN LOGINUSERID VARCHAR(10),IN PICKLIST_CODE VARCHAR(50),IN LOTCODENUMS VARCHAR(3000))
BEGIN
DECLARE LOCATIONID,LNF,LNS,F_PICKINGLIST_ID INT;
DECLARE LOTCODENUM VARCHAR(2000);
DECLARE LOT_CODE VARCHAR(30);
DECLARE LOTNUM DOUBLE;
DECLARE TEMP_V VARCHAR(100);
DECLARE BLNRETURN INT DEFAULT -1;
SET F_PICKINGLIST_ID = (SELECT PICKINGLIST_ID FROM VMI_PICKINGLIST_PO WHERE PICKINGLIST_CODE = TRIM(PICKLIST_CODE));
SET LOTCODENUM = LOTCODENUMS;
START TRANSACTION;
L_LOOP:LOOP
IF TRIM(LOTCODENUM) ="" THEN
LEAVE L_LOOP;
END IF;
SET LNF = INSTR(LOTCODENUM,",");
IF LNF = 0 THEN
LEAVE L_LOOP;
END IF;
SET TEMP_V = SUBSTR(LOTCODENUM,1,LNF-1);
SET LNS = INSTR(TEMP_V,"#");
