SET STOCK_NUM = (SELECT COUNT(*) FROM VMI_STOCK WHERE WAREHOUSE_ID = WAREHOUSEID_V AND MATERIAL_ID = MATERIAL_ID_V AND VENDOURID = VENDOR_ID_V);

