CREATE DEFINER=`gsp`@`localhost` FUNCTION `bid_all_amount`(v_org_id integer,v_company_id integer,v_begin_date varchar(10),v_end_date varchar(10)) RETURNS double
BEGIN 
         DECLARE all_amount double;
         select  round(sum(rr.fc_min_require_amount),2) into all_amount
         from bid_rfx_head rh, (select distinct rr.rfx_head_id,rr.fc_history_cost_amount,
         rr.fc_min_require_amount from bid_rfx_rank rr where  rr.rank = 1
         and rr.passel_id=0
         and rr.item_id=0) rr
         where rh.rfx_oper_unit = v_org_id
           and rh.company_id = v_company_id
           and rh.create_time >= STR_TO_DATE(v_begin_date, 'yyyy-mm-dd')
           and rh.create_time <= STR_TO_DATE(v_end_date, 'yyyy-mm-dd')
           and rr.rfx_head_id = rh.rfx_head_id
           and rh.istest=1;
         Return all_amount;
    END
;;
