CREATE DEFINER=`gsp`@`localhost` FUNCTION `bid_capability_item_rank`(v_rfx_head_id integer,v_passel_id integer,v_item_id integer, v_total_score double ) RETURNS int(11)
begin   
        
        declare seq_value integer default 0;  
 select  ifnull(count(1),0) +1 as rank  into  seq_value from (
                          select tmp.rfx_head_id,
                                 tmp.vendor_id,
                                 tmp.passel_id,
                                 tmp.item_id,
                                 head.RFX_TYPE,
                                 rfi.convert_result,
                                 (rfi.convert_result + (case
                                   when (head.rfx_type = 5 and rfx.avg = 0) or
                                        (head.rfx_type = 6 and rfx.avg = 0) then
                                    0
                                   when head.rfx_type = 5 and
                                        tmp.fc_min_require_amount != 0 then
                                    (rfx.avg / tmp.fc_min_require_amount) *
                                    line.rfx_bid_percent * 100
                                   when head.rfx_type = 6 and
                                        tmp.fc_min_require_amount != 0 then
                                    (tmp.fc_min_require_amount / rfx.avg) *
                                    line.rfx_bid_percent * 100
                                 end)) total_score
                            from (select *
                                    from bid_rfx_rank
                                   where rfx_head_id = v_rfx_head_id) tmp,
                                 bid_rfx_head head,
                                 bid_rfx_line line,
                                 (select *
                                    from bid_rfx_result_setting setting
                                   where rfx_head_id = v_rfx_head_id) rfi,
                                 (select tmp.rfx_head_id,
                                         tmp.passel_id,
                                         tmp.item_id,
                                         avg(fc_min_require_amount) avg
                                    from (select *
                                            from bid_rfx_rank
                                           where rfx_head_id = v_rfx_head_id) tmp
                                   where tmp.vendor_id > 0
                                     and tmp.item_id > 0
                                   group by tmp.rfx_head_id,
                                            tmp.passel_id,
                                            tmp.item_id) rfx
                           where head.rfx_head_id = tmp.rfx_head_id
                             and line.rfx_head_id = head.rfx_head_id
                             and tmp.vendor_id = rfi.vendor_id
                             and tmp.rfx_head_id = rfi.rfx_head_id
                             and tmp.passel_id = rfx.passel_id
                             and tmp.item_id = rfx.item_id 
) a where   a.rfx_head_id=v_rfx_head_id
and a.passel_id=v_passel_id
and a.item_id=v_item_id
and total_score>v_total_score;
   return seq_value;    
    end
;;
DELIMITER ;

