CREATE DEFINER=`gsp`@`localhost` FUNCTION `bid_count_history_cost`(v_rfx_head_id integer,v_passel_id integer,v_item_id integer) RETURNS double
BEGIN
       DECLARE history_cost double;
       if(v_rfx_head_id<>0 and v_passel_id=0 and v_item_id=0) then
          select sum(rs.fc_min_require_num*rs.fc_history_cost) into history_cost from  bid_rfx_showtable rs
          where rs.rfx_head_id = v_rfx_head_id;
       end if;
       if(v_rfx_head_id<>0 and v_passel_id<>0 and v_item_id=0) then
          select sum(rs.fc_min_require_num*rs.fc_history_cost) into history_cost from  bid_rfx_showtable rs
